import React from 'react'

const Question = (props) => {

    const { text, index } = props.question

    const disabledCheck = props.answeredQuestions.includes(index) ? true : false

    return (
        <div>
            <h4>{text}</h4>

            <button 
                disabled={disabledCheck}
                onClick={(event) => {props.checkAnswer(true, props.question, event)}}
                >True
            </button>
            <button 
                disabled={disabledCheck}
                onClick={(event) => {props.checkAnswer(false, props.question, event)}}
            >False
            </button>
        </div>
    )
}

export default Question
