import React, {Fragment} from 'react'
import Data from './data'
import Header from './Header'
import QuestionsContainer from './QuestionsContainer'

class Container extends React.Component {

    state = {
        questions: Data.questions,
        score: 0
    }

    checkAnswer = (userAnswer, question, event) => {
        if (question.answer === userAnswer) {
            event.target.style.backgroundColor = 'green'
            this.setState({score: this.state.score + 1})
        } else {
            event.target.style.backgroundColor = 'red'
        }

        // Disabling the buttons
        event.target.parentNode.children[1].disabled = true
        event.target.parentNode.children[2].disabled = true
    }

    resetGame = () => {
        debugger
    }

    render() {
        return (
            <Fragment>
                <Header score={this.state.score} resetGame={this.resetGame} />
                <QuestionsContainer questions={this.state.questions} checkAnswer={this.checkAnswer} />
            </Fragment>
        )
    }
}

export default Container
