import React from 'react'

const Question = (props) => {

    const { answer, text, checkAnswer } = props.question

    return (
        <div>
            <h4>{text}</h4>

            <button onClick={(event) => {this.checkAnswer(true, props.question, event)}}>True</button>
            <button onClick={(event) => {this.checkAnswer(false, props.question, event)}}>False</button>
        </div>
    )
}

export default Question
