import React, {Fragment} from 'react'
import Data from './data'
import Header from './Header'
import QuestionsContainer from './QuestionsContainer'

class Container extends React.Component {

    state = {
        questions: [],
        score: 0,
        answeredQuestions: []
    }

    componentDidMount() {
        this.setState({
            questions: Data.questions.map((question, index) => {return {...question, index: index}})
        })
    }

    checkAnswer = (userAnswer, question, event) => {
        let score = this.state.score

        if (question.answer === userAnswer) {
            event.target.style.backgroundColor = 'green'
            score = score + 1
        } else {
            event.target.style.backgroundColor = 'red'
        }

        // Disabling the buttons
        event.target.parentNode.children[1].disabled = true
        event.target.parentNode.children[2].disabled = true

        this.setState({
            score: score,
            answeredQuestions: [...this.state.answeredQuestions, question.index]
        })
    }

    resetGame = () => {
        this.setState({
            score: 0,
            answeredQuestions: []
        })

        // document.querySelectorAll('button').forEach(button => {
        //     button.disabled = false
        //     button.style.backgroundColor = '#aaaaaa'
        // })
    }

    render() {
        return (
            <Fragment>
                <Header score={this.state.score} resetGame={this.resetGame} />
                <QuestionsContainer 
                    questions={this.state.questions} 
                    checkAnswer={this.checkAnswer} 
                    answeredQuestions={this.state.answeredQuestions} />
            </Fragment>
        )
    }
}

export default Container
